import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3333');

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [scooterData, setScooterData] = useState(null);

  useEffect(() => {
    // Listen for the 'weatherData' event
    socket.on('weatherData', (data) => {
      console.log('Received weather data:', data);
      setWeatherData(data);
    });

    // Listen for the 'scooterData' event
    socket.on('scooterData', (data) => {
      console.log('Received scooter data:', data);
      setScooterData(data);
    });

    // Clean up the event listeners
    return () => {
      socket.off('weatherData');
      socket.off('scooterData');
    };
  }, []);

  return (
    <div>
      <h1>Amsterdam City Data</h1>
      {weatherData && (
        <div>
          <h2>Weather Data</h2>
          <p>Temperature: {weatherData.temperature}</p>
          <p>Humidity: {weatherData.humidity}</p>
          <p>Wind speed: {weatherData.windSpeed}</p>
          <p>Weather condition: {weatherData.condition}</p>
        </div>
      )}
      {scooterData && (
        <div>
          <h2>Scooter Data</h2>
          <ul>
            {scooterData.map((scooter) => (
              <li key={scooter.id}>
                Scooter {scooter.id}: battery level {scooter.batteryLevel}, location ({scooter.lat}, {scooter.lng})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
